	public static void main(String[] args) throws Exception {
		is = oj ? System.in : new ByteArrayInputStream(INPUT.getBytes());
		out = new PrintWriter(System.out);

		long s = System.currentTimeMillis();

		int T = ni();
		for (int t = 0; t < T; t++) {
			if (t == 28 || t == 38 || t == 39 || t == 49 || t == 97) {
				int u = 0;
				u++;
			}
			solve();
		}

		out.flush();
		tr(System.currentTimeMillis() - s + "ms");
	}

	static int[] nearestAlts = new int[100010];
	static int[] minCuts = new int[100010];

	/**
	 * @solution:
	 */
	static void solve() {
		int N = ni(); // 100K
		int K = ni(); // 100K
		char[] bits = ns().toCharArray();

		Arrays.fill(nearestAlts, Integer.MAX_VALUE);
		Arrays.fill(minCuts, Integer.MAX_VALUE);

		int j = 0;
		for (int i = 1; i < N; i++) {
			if (bits[i - 1] == bits[i]) {
				for (; j < i; j++) {
					nearestAlts[j] = i;
				}
			}
		}

		int n0 = nearestAlts[0];
		for (int i = 0; i < n0 && i < K; i++) {
			minCuts[i] = i;
		}
		for (int i = n0; i < K; i++) {
			minCuts[i] = 0;
		}

		for (int i = K; i < N; i++) {
			minCuts[i] = Math.min(minCuts[i], minCuts[i - 1] + 1);
			int start = i - K + 1;
			if (nearestAlts[start] <= i) {
				minCuts[i] = Math.min(minCuts[i], minCuts[start - 1] + 1);
			}
			int ns = nearestAlts[start - 1];
			if (ns < i && nearestAlts[ns + 1] <= i) {
				minCuts[i] = Math.min(minCuts[i], minCuts[ns] + 1);
			}
		}

		out.println(minCuts[N - 1]);
	}

	/*****************************************************************
	 ******************** BASIC READER *******************************
	 *****************************************************************/

	static byte[] inbuf = new byte[1024];
	static int lenbuf = 0, ptrbuf = 0;

	static int readByte() {
		if (lenbuf == -1) throw new InputMismatchException();
		if (ptrbuf >= lenbuf) {
			ptrbuf = 0;
			try {
				lenbuf = is.read(inbuf);
			} catch (IOException e) {
				throw new InputMismatchException();
			}
			if (lenbuf <= 0) return -1;
		}
		return inbuf[ptrbuf++];
	}

	static boolean isSpaceChar(int c) {
		return !(c >= 33 && c <= 126);
	}

	static int skip() {
		int b;
		while ((b = readByte()) != -1 && isSpaceChar(b));
		return b;
	}

	static double nd() {
		return Double.parseDouble(ns());
	}

	static char nc() {
		return (char) skip();
	}

	static String ns() {
		int b = skip();
		StringBuilder sb = new StringBuilder();
		while (!(isSpaceChar(b))) {
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	static char[] ns(int n) {
		char[] buf = new char[n];
		int b = skip(), p = 0;
		while (p < n && !(isSpaceChar(b))) {
			buf[p++] = (char) b;
			b = readByte();
		}
		return n == p ? buf : Arrays.copyOf(buf, p);
	}

	static char[][] nm(int n, int m) {
		char[][] map = new char[n][];
		for (int i = 0; i < n; i++)
			map[i] = ns(m);
		return map;
	}

	static int[] na(int n) {
		int[] a = new int[n];
		for (int i = 0; i < n; i++)
			a[i] = ni();
		return a;
	}

	static int ni() {
		int num = 0, b;
		boolean minus = false;
		while ((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if (b == '-') {
			minus = true;
			b = readByte();
		}

		while (true) {
			if (b >= '0' && b <= '9') {
				num = num * 10 + (b - '0');
			} else {
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	static long nl() {
		long num = 0;
		int b;
		boolean minus = false;
		while ((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if (b == '-') {
			minus = true;
			b = readByte();
		}

		while (true) {
			if (b >= '0' && b <= '9') {
				num = num * 10 + (b - '0');
			} else {
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	static boolean oj = System.getProperty("ONLINE_JUDGE") != null;

	static void tr(Object... o) {
		if (!oj) {
			System.out.println(Arrays.deepToString(o));
		}
	}
